import { useForm } from "react-hook-form"



const MainAdvertisement = () => {
        const { register, handleSubmit, formState } = useForm() 

        const onSubmit = async data => {
               console.log(data)
        }
        
        return ( 
                <div className="container">
                
                        <form onSubmit={handleSubmit(onSubmit)}>
                                <div class="mb-3">
                                        <label for="sendImage" class="form-label">Images for main advertisement</label>
                                        <input class="form-control" type="file" multiple {...register("sendImage", {required:true})} />
                                        { formState.errors.sendImage?.type === "required" && "New image advertisement necessary." }
                                </div>
                                <div class="mb-3">
                                        <label for="title" class="form-label">Title</label>
                                        <input type="text" class="form-control" {...register("title", {required:true, maxLength:30})} />
                                        { formState.errors.title?.type === "required" && "Title must be filled." }
                                </div>
                                <div class="mb-3">
                                        <label for="description" class="form-label">Description</label>
                                        <textarea class="form-control" rows="3" {...register("description", {required:true, maxLength:4})}></textarea>
                                        { formState.errors.description?.type === "required" && "Description must be filled." }
                                </div>
                                <button type="submit" class="btn btn-primary">Submit</button>
                        </form>
                </div>
         );
}
 
export default MainAdvertisement;
