import { useForm } from "react-hook-form"
import "../admin/admin.css"
import { useState } from "react"



const Login = () => {

        const [msg, setMsg] = useState("")

        const {register, handleSubmit, formState} = useForm()
        const loginSend = async (data) => {
                console.log("a1: ", data)
                const userInput = {
                        email: data.email,
                        password: data.password
                }

        }

        return ( 
                <div className="container py-">
                        <h1>User Login</h1>
                        <form onSubmit={handleSubmit(loginSend)}>
                                <div class="form-floating mb-3">
                                        <input type="email" class="form-control" id="floatingInput" {...register("email", {required:true})} />
                                        <label for="floatingInput">Email address</label>
                                        {formState.errors?.type === "required" && <p className="note">Email must be filled.</p>}
                                </div>
                                <div class="form-floating">
                                        <input type="password" class="form-control" id="floatingPassword" {...register("password", {required:true})} />
                                        <label for="floatingPassword">Password</label>
                                        {formState.errors?.type === "required" && <p className="note">Password must be filled.</p>}
                                </div>
                                <div class="mb-3">
                                        <button class="p-2 btn btn-primary">Login</button>
                                </div>
                        </form>                
                        { msg }
                </div>
         );
}
 
export default Login;
