import { useEffect, useState } from "react"


const Carousel = ({items, idArray}) => {
       
        console.log("aaa: ", items, idArray)

        return ( 
                <div id="carouselExampleDark" className="carousel carousel-dark slide" data-bs-interval="10000" data-bs-ride="carousel ">
                        <div className="carousel-indicators">
                                <button type="button" data-bs-target="#carouselExampleDark" data-bs-slide-to="0" className="active" aria-current="true" aria-label="Slide 1"></button>
                                <button type="button" data-bs-target="#carouselExampleDark" data-bs-slide-to="1" aria-label="Slide 2"></button>
                                <button type="button" data-bs-target="#carouselExampleDark" data-bs-slide-to="2" aria-label="Slide 3"></button>
                        </div>
                        <div className="carousel-inner">
                        { idArray.map(id => {
                                return (
                                        <div className="carousel-item active" key={items}>
                                                <img src={items[itemId].img_url} className="d-block w-100" alt="carousel-image" />
                                                <div className="carousel-caption d-none d-md-block">
                                                        <h5>{items[itemId].title}</h5>
                                                        <p>{items[itemId].description}</p>
                                                </div>
                                        </div>
                                )
                        })
                                 
                        }
                                
                        </div>
                        <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleDark" data-bs-slide="prev">
                                <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                                <span className="visually-hidden" onClick={() => active("prev")}>Previous</span>
                        </button>
                        <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleDark" data-bs-slide="next">
                                <span className="carousel-control-next-icon" aria-hidden="true"></span>
                                <span className="visually-hidden" onClick={() => active("next")}>Next</span>
                        </button>
                </div>
         );
}
 

export default Carousel;
