import AdminAdvertisementForm from "./AdminAdvertisementForm";
import apiAxios from "../../config/axiosConfig"
import { useForm } from "react-hook-form"
import { useState } from "react";


const AdminAdvertisementUpdate = ({id, img_url, title, description}) => {

        const { register, handleSubmit, formState } = useForm() 
        const [msg, setMsg] = useState("")

        const onUpdate = async data => {
                try {   
                        const response = await apiAxios.put(
                                "/auth/admin/advertisement",
                                {
                                        method: "update",
                                        imageAd: data.imageAd,
                                        title: data.title,
                                        description: data.description,
                                }
                                // {withCredentials: true}
                        )

                        if (response.status === 200) {
                                setMsg("A new advertisement has been added.")
                        }
                } catch (error) {
                        const errorMsg = error.response.data.error ? error.response.data.error.message : "An error occurred."
                        setMsg(errorMsg)
                }
        }


        return (
                <div className="container m-3">
                        <h3>Update Admin Advertisement: </h3>
                        <form handleSubmit={handleSubmit(onUpdate)} register={register} formState={formState} buttonType="put">
                                <AdminAdvertisementForm />
                        </form> 
                        {msg && <div className="my-3">{msg}</div>}
                </div>
         );
}
 

export default AdminAdvertisementUpdate;
